mapper.insert:
insert into sys_user_role(role_id, user_id)
select sys_role.role_id, #{userId} from sys_role
    where #{userRoleRank} < sys_role.role_rank and sys_role.role_rank <= #{updateRoleRank};

mapper.delete:
delete from sys_user_role
where  user_id = #{userId} and role_id in (
    select sys_role.role_id from sys_role
    where #{updateRoleRank} < sys_role.role_rank and sys_role.role_rank <= #{userRoleRank};
);

mapper.update:
update sys_user
set password = #{defultPassword}
where id = #{userId};

mapper.select:
SELECT role_id, role_name
FROM sys_role
WHERE role_rank < #{baseContextRoleRank};
